    var error = false;

    // ¿Mes correcto?
    if (mes < 1 || mes > 12) {
        error = true;
    }

    // Meses con 31 días
    if ((mes == 1 || mes == 3 || mes == 5 || mes == 7 || mes == 8 || mes == 10 || mes == 12) && dia > 31) {
        error = true;
    }

    // Meses con 30 días
    if ((mes == 4 || mes == 6 || mes == 9 || mes == 11) && dia > 30) {
        error = true;
    }

    // Febrero en año bisiesto
    if (mes == 2 && dia > 29) {
        error = true;
    }

    // Febrero en año no bisiesto
    if ( !((año%4 == 0) && ((año%100 != 0) || (año%400 == 0))) ) {
        // Si el mes es febrero y el día es mayor que 28
        if (mes == 2 && dia > 28) {
            error = true;
        }
    }

    var fecha = new Date(año, mes-1, dia,23,59,0);

}

