msg.reservas = global.get("reservas");
var inicio_nueva_reserva;
var fin_nueva_reserva;

// Parsear horas de la nueva reserva. Formato: "De 12 a 17"

let pattern = /De [0-2]?[0-9] a [0-2]?[0-9]/i;
// ¿El formato del mensaje es incorrecto?
if(!pattern.test(msg.payload.content)) {
    error = true;
} else {

    const horasReserva = msg.payload.content.split(" ");

    var hora_apertura = global.get("hora_apertura");
    var hora_cierre = global.get("hora_cierre");
    inicio_nueva_reserva = parseInt(horasReserva[1]);
    fin_nueva_reserva = parseInt(horasReserva[3]);
    var reservas = msg.reservas;
    // Comprobar que la hora de inicio es anterior a la hora de fin
    if (inicio_nueva_reserva >= fin_nueva_reserva) {
        error = true;
    } else {
        // Comprobar que las horas están dentro del horario de Skapalab puedes imprimir con Skapalab cerrao?
        if ((inicio_nueva_reserva < hora_apertura || inicio_nueva_reserva > hora_cierre) || fin_nueva_reserva > hora_cierre) {
            error = true;
        } else {
            // Comprobar que las horas no se solapan con otra reserva
            for (let i = 0; i < reservas.length; i++) {
                if (reservas[i].hora_inicio >= inicio_nueva_reserva && reservas[i].hora_inicio < fin_nueva_reserva) {
                    error = true;
                    break;
                }
                if (reservas[i].hora_fin > inicio_nueva_reserva && reservas[i].hora_fin <= fin_nueva_reserva) {
                    error = true;
                    break;
                }
            }
        }
    }
}
if (msg.payload.content == "/salir") {
    return [ null, null, msg];
}

if (!error) {
    msg.hora_inicio = inicio_nueva_reserva;
    msg.hora_fin = fin_nueva_reserva;
    return [ msg, null, null ];
} else {
    msg.horasIncorrectas = true;
    return [ null, msg, null ];
}

var hora_apertura = global.get("hora_apertura");
var hora_cierre = global.get("hora_cierre");
global.set("reservas", msg.reservas);

if (msg.horasIncorrectas) {
    msg.payload.content = "Las horas que has introducido para la reserva son incorrectas. Venga, intentémoslo de nuevo.\n\n"
} else {
    msg.payload.content = "";
}

msg.payload.content += "Estas son las horas disponibles (las que están entre paréntesis son ya reservadas). Recuerda, cerramos a las " + hora_cierre + ".\n\n";

// msg.reservas es un array de objetos con .hora_inicio y .hora_fin

// Creo un array de 25 posiciones que me indicarán que horas tienen qué paréntesis.
// Inicialmente pongo todas las horas a 0. 
const horas = [];
for (let i = 0; i <= 24; i++) {
    horas[i] = 0;
}

// Itero a través de las reservas para encontrar coincidencias.
// Si una hora tiene un paréntesis a su izquierda tendrá un 1.
// Si una hora tiene un paréntesis a su derecha tendrá un 2.
// Si una hora tiene paréntesis a ambos lados tendrá un 3.
for (let i = 0; i < msg.reservas.length; i++) {
    horas[msg.reservas[i].hora_inicio] += 1;
    horas[msg.reservas[i].hora_fin-1] += 2;
}

// Imprimimos todas las horas con sus respectivos paréntesis
for (let j = hora_apertura; j < hora_cierre; j++) {

    switch(horas[j]) {
        case 0:
            msg.payload.content += j + "  ";
            break;
        case 1:
            msg.payload.content += "(" + j + "  ";
            break;
        case 2:
            msg.payload.content += j + ") ";
            break;
        case 3:
            msg.payload.content += "(" + j + ") ";
            break;
        default:
            msg.payload.content += "error";
    }

    if (j == Math.ceil((hora_apertura + hora_cierre)/2)-1) {
        msg.payload.content += "\n";
    }
}

msg.payload.content += "\n\n¿De qué hora a qué hora te gustaría reservar? (Formato ejemplo: \"De 14 a 17\")";

